// Get the raw JSON output from the first "Generate Complete Blog" node
const raw = $('Insert Images into Content').first().json.output;

// Clean unwanted JS lines and code fences from the output
const cleaned = raw
  .replace(/let cleaned = raw\.replace\(.*?\);/gs, '') // remove unwanted JS line if present
  .replace(/```(json|html)?/g, '') // remove code fences like ```json or ```html
  .trim();

return {
  cleanedOutput: cleaned
};
