let html = $json.body_html || '';

// remove social bar
html = html.replace(/<div class="horizontal-social-bar">[\s\S]*?<\/div>/i, ' ');

// remove ad containers that often look like <div id="Ads_..."> ... </div>
html = html.replace(/<div[^>]+id="Ads_[^"]+"[\s\S]*?<\/div>/gi, ' ');

// remove script blocks anywhere
html = html.replace(/<script[\s\S]*?<\/script>/gi, ' ');
html = html.replace(/<style[\s\S]*?<\/style>/gi, ' ');

// cut off everything from the "Das könnte Sie auch interessieren" paragraph onward
html = html.replace(/<p>\s*<strong>\s*Das könnte Sie auch interessieren:\s*<\/strong>\s*<\/p>[\s\S]*$/i, ' ');

// also remove the tag list if it is still present
html = html.replace(/<div class="taglist--v2">[\s\S]*$/i, ' ');

// convert to text, keep some structure
let text = html
  .replace(/<h2[^>]*>/gi, '\n\n')
  .replace(/<\/h2>/gi, '\n')
  .replace(/<p[^>]*>/gi, '\n')
  .replace(/<\/p>/gi, '\n')
  .replace(/<br\s*\/?>/gi, '\n')
  .replace(/<[^>]+>/g, ' ')
  .replace(/&nbsp;/g, ' ')
  .replace(/&amp;/g, '&')
  .replace(/&quot;/g, '"')
  .replace(/&#39;/g, "'")
  .replace(/\s+\n/g, '\n')
  .replace(/\n\s+/g, '\n')
  .replace(/[ \t]+/g, ' ')
  .trim();

return {
  url: $json.url,
  body_text: text,
};

const link = $json.link || '';
const text = ($json.body_text || '').trim();
const maxLen = 2500;

const body_text_short = text.length > maxLen ? text.slice(0, maxLen) + '...' : text;

// cheap stable id without crypto
const article_id = Buffer.from(link).toString('base64').slice(0, 24);

return {
  ...$json,
  article_id,
  body_text_short,
};

return [{
  json: {
    drive_files: items.map(i => ({
      id: i.json.id,
      name: i.json.name,
      mimeType: i.json.mimeType,
      modifiedTime: i.json.modifiedTime,
    }))
  }
}];

function toObject(v) {
  if (!v) return null;
  if (typeof v === 'string') {
    try { return JSON.parse(v); } catch (e) { return null; }
  }
  if (typeof v === 'object') return v;
  return null;
}

const results = items
  .map(i => toObject(i.json.output ?? i.json))
  .filter(Boolean);

const rank = (r) => Number(r ?? 0);
results.sort((a, b) => rank(b.relevance) - rank(a.relevance));

const top3 = results.slice(0, 3);

const impactedByFile = {};
for (const a of results) {
  for (const f of (a.impacted_files || [])) {
    if (!f?.file_id || !f?.file_name) continue;

    if (!impactedByFile[f.file_id]) {
      impactedByFile[f.file_id] = {
        file_id: f.file_id,
        file_name: f.file_name,
        actions: []
      };
    }

    impactedByFile[f.file_id].actions.push({
      action: f.action,
      recommended_change: f.recommended_change,
      source_article: { title: a.title, link: a.link, date: a.date, relevance: a.relevance }
    });
  }
}

const missingDocs = [];
const seen = new Set();
for (const a of results) {
  for (const m of (a.missing_docs || [])) {
    const name = (m.proposed_name || '').trim();
    const key = name.toLowerCase();
    if (!name || seen.has(key)) continue;
    seen.add(key);

    missingDocs.push({
      proposed_name: name,
      why: m.why,
      source_article: { title: a.title, link: a.link }
    });
  }
}

return [{
  json: {
    top3_articles: top3.map(a => ({
      title: a.title,
      link: a.link,
      date: a.date,
      relevance: a.relevance,
      summary: a.summary
    })),
    impacted_files: Object.values(impactedByFile),
    missing_doc_ideas: missingDocs,
    all_articles: results
  }
}];
