parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "validationStatus": {
      "type": "string",
      "description": "Validation result: VALID | INVALID | REQUIRES_REVIEW"
    },
    "dataSubjectId": {
      "type": "string",
      "description": "Unique identifier for the data subject"
    },
    "requestType": {
      "type": "string",
      "description": "Type of request: ACCESS | DELETION | RECTIFICATION | PORTABILITY | RESTRICTION"
    },
    "consentStatus": {
      "type": "string",
      "description": "Current consent status from lookup"
    },
    "retentionStatus": {
      "type": "string",
      "description": "Retention policy status from lookup"
    },
    "complianceViolations": {
      "type": "array",
      "description": "List of any compliance violations found"
    },
    "dataMinimizationRequired": {
      "type": "boolean",
      "description": "Whether data minimization is required"
    },
    "reasoning": {
      "type": "string",
      "description": "Explanation of the validation decision"
    }
  },
  "required": ["validationStatus", "dataSubjectId", "requestType", "reasoning"]
}

{
  "type": "object",
  "properties": {
    "actionType": {
      "type": "string",
      "description": "Action to take: FULFILLMENT | REGULATORY_REPORT | ESCALATION"
    },
    "dataSubjectId": {
      "type": "string",
      "description": "Unique identifier for the data subject"
    },
    "fulfillmentActions": {
      "type": "array",
      "description": "List of specific fulfillment actions to execute"
    },
    "reportRequired": {
      "type": "boolean",
      "description": "Whether regulatory report is required"
    },
    "escalationReason": {
      "type": "string",
      "description": "Reason for compliance escalation if applicable"
    },
    "dataMinimizationApplied": {
      "type": "boolean",
      "description": "Whether data minimization was applied"
    },
    "auditLog": {
      "type": "array",
      "description": "Audit trail of governance decisions"
    },
    "reasoning": {
      "type": "string",
      "description": "Explanation of the governance decision"
    }
  },
  "required": ["actionType", "dataSubjectId", "reasoning"]
}

{
  "type": "object",
  "properties": {
    "consentStatus": {
      "type": "string",
      "description": "Current consent status"
    },
    "legalBasis": {
      "type": "string",
      "description": "Legal basis for processing"
    },
    "consentDate": {
      "type": "string",
      "description": "ISO timestamp of consent"
    },
    "expirationDate": {
      "type": "string",
      "description": "ISO timestamp of expiration or null"
    },
    "purposes": {
      "type": "array",
      "description": "Processing purposes"
    },
    "violations": {
      "type": "array",
      "description": "Consent violations found"
    }
  },
  "required": ["consentStatus", "legalBasis"]
}

{
  "type": "object",
  "properties": {
    "retentionStatus": {
      "type": "string",
      "description": "Retention status"
    },
    "dataCategories": {
      "type": "array",
      "description": "Data categories and retention periods"
    },
    "expirationDate": {
      "type": "string",
      "description": "ISO timestamp of expiration"
    },
    "daysUntilExpiration": {
      "type": "number",
      "description": "Days remaining"
    },
    "shouldDelete": {
      "type": "boolean",
      "description": "Whether data should be deleted"
    },
    "retentionViolations": {
      "type": "array",
      "description": "Retention violations"
    }
  },
  "required": ["retentionStatus", "shouldDelete"]
}

{
  "type": "object",
  "properties": {
    "reportId": {
      "type": "string",
      "description": "Unique report identifier"
    },
    "reportType": {
      "type": "string",
      "description": "Type of regulatory report"
    },
    "dataSubjectId": {
      "type": "string",
      "description": "Subject of the report"
    },
    "requestDate": {
      "type": "string",
      "description": "ISO timestamp"
    },
    "actionsTaken": {
      "type": "array",
      "description": "Fulfillment actions"
    },
    "dataMinimizationApplied": {
      "type": "boolean",
      "description": "Data minimization applied"
    },
    "auditTrail": {
      "type": "array",
      "description": "Audit events"
    },
    "complianceStatus": {
      "type": "string",
      "description": "Compliance status"
    }
  },
  "required": ["reportId", "reportType", "complianceStatus"]
}

