parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "contentId": {
      "type": "string",
      "description": "Unique identifier for the content"
    },
    "toxicityScore": {
      "type": "number",
      "description": "Toxicity score from 0 to 1"
    },
    "spamScore": {
      "type": "number",
      "description": "Spam likelihood score from 0 to 1"
    },
    "engagementAnomaly": {
      "type": "boolean",
      "description": "Whether engagement patterns are anomalous"
    },
    "engagementAnomalyScore": {
      "type": "number",
      "description": "Anomaly score for engagement patterns"
    },
    "behaviorFlags": {
      "type": "array",
      "items": {"type": "string"},
      "description": "List of behavior flags detected"
    },
    "contentCategory": {
      "type": "string",
      "description": "Category of content"
    },
    "riskLevel": {
      "type": "string",
      "enum": ["low", "medium", "high", "critical"],
      "description": "Overall risk level"
    },
    "reasoning": {
      "type": "string",
      "description": "Explanation of the validation assessment"
    }
  },
  "required": ["contentId", "toxicityScore", "spamScore", "engagementAnomaly", "riskLevel", "reasoning"]
}

{
  "type": "object",
  "properties": {
    "action": {
      "type": "string",
      "enum": ["approve", "flag", "remove", "suspend_creator", "demonetize"],
      "description": "Enforcement action to take"
    },
    "severity": {
      "type": "string",
      "enum": ["low", "medium", "high"],
      "description": "Severity level of the issue"
    },
    "requiresHumanReview": {
      "type": "boolean",
      "description": "Whether human moderator review is required"
    },
    "monetizationImpact": {
      "type": "string",
      "enum": ["none", "warning", "temporary_suspension", "permanent_demonetization"],
      "description": "Impact on creator monetization"
    },
    "enforcementDetails": {
      "type": "object",
      "properties": {
        "reason": {"type": "string"},
        "duration": {"type": "string"},
        "appealable": {"type": "boolean"}
      }
    },
    "reasoning": {
      "type": "string",
      "description": "Explanation of the governance decision"
    }
  },
  "required": ["action", "severity", "requiresHumanReview", "monetizationImpact", "reasoning"]
}

parameters.jsonBody:
={
  "contentId": $fromAI('contentId', 'Content identifier', 'string'),
  "action": $fromAI('enforcementAction', 'Enforcement action: flag, remove, or restrict', 'string'),
  "reason": $fromAI('reason', 'Reason for enforcement action', 'string'),
  "appealable": $fromAI('appealable', 'Whether the action can be appealed', 'boolean', true)
}
