// Merge webhook student data with learning history from API
const webhookData = $input.first().json;
const learningHistory = $input.last().json;

// Combine all student data for validation
const mergedData = {
  studentId: webhookData.studentId,
  enrollmentData: webhookData.enrollmentData || {},
  assessmentData: webhookData.assessmentData || {},
  learningSignals: webhookData.learningSignals || {},
  learningHistory: learningHistory || {},
  submittedAt: webhookData.submittedAt || new Date().toISOString(),
  institutionalPolicies: webhookData.institutionalPolicies || {}
};

return [{ json: mergedData }];

