// Log comprehensive compliance audit trail
const timestamp = new Date().toISOString();
const inputData = $input.first().json;

// Determine log type based on source
let logType = 'UNKNOWN';
let logData = {};

if (inputData.output && inputData.output.validationStatus) {
  // Validation log
  logType = 'VALIDATION';
  logData = {
    validationStatus: inputData.output.validationStatus,
    enrollmentValid: inputData.output.enrollmentValid,
    assessmentValid: inputData.output.assessmentValid,
    learningSignalsValid: inputData.output.learningSignalsValid,
    ferpaCompliant: inputData.output.ferpaCompliant,
    accreditationCompliant: inputData.output.accreditationCompliant,
    complianceScore: inputData.output.complianceScore,
    validationIssues: inputData.output.validationIssues || [],
    reasoning: inputData.output.reasoning
  };
} else if (inputData.output && inputData.output.actionType) {
  // Orchestration action log
  logType = 'ORCHESTRATION';
  logData = {
    actionType: inputData.output.actionType,
    priority: inputData.output.priority,
    complianceRequirements: inputData.output.complianceRequirements || [],
    reasoning: inputData.output.reasoning,
    actionDetails: inputData.output
  };
}

// Create comprehensive audit log entry
const auditLog = {
  timestamp: timestamp,
  logType: logType,
  studentId: inputData.studentId || 'UNKNOWN',
  workflowExecutionId: $execution.id,
  logData: logData,
  complianceFrameworks: {
    ferpa: true,
    accreditation: $('Workflow Configuration').first().json.accreditationStandard || 'UNKNOWN'
  },
  auditTrailComplete: true
};

console.log('Compliance Audit Log:', JSON.stringify(auditLog, null, 2));

return [{ json: auditLog }];
