parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "complianceStatus": {
      "type": "string",
      "description": "Overall compliance status: Compliant, Non-Compliant, or Critical"
    },
    "policyViolations": {
      "type": "array",
      "description": "List of identified policy violations",
      "items": {
        "type": "object",
        "properties": {
          "policyId": {"type": "string"},
          "violationType": {"type": "string"},
          "severity": {"type": "string"},
          "description": {"type": "string"}
        }
      }
    },
    "performanceMetrics": {
      "type": "object",
      "description": "Key performance indicators",
      "properties": {
        "adherenceScore": {"type": "number"},
        "riskLevel": {"type": "string"},
        "programEffectiveness": {"type": "number"}
      }
    },
    "recommendations": {
      "type": "array",
      "description": "Recommended corrective actions",
      "items": {"type": "string"}
    },
    "reasoning": {
      "type": "string",
      "description": "Detailed reasoning for the compliance assessment"
    }
  },
  "required": ["complianceStatus", "policyViolations", "performanceMetrics", "recommendations", "reasoning"]
}

{
  "type": "object",
  "properties": {
    "reportSummary": {
      "type": "string",
      "description": "Executive summary of compliance findings"
    },
    "stakeholderNotification": {
      "type": "string",
      "description": "Message for stakeholder notification"
    },
    "escalationRequired": {
      "type": "boolean",
      "description": "Whether escalation to senior leadership is required"
    },
    "actionPlan": {
      "type": "array",
      "description": "Prioritized action items",
      "items": {
        "type": "object",
        "properties": {
          "action": {"type": "string"},
          "priority": {"type": "string"},
          "owner": {"type": "string"},
          "deadline": {"type": "string"}
        }
      }
    },
    "auditNotes": {
      "type": "string",
      "description": "Detailed notes for audit trail"
    },
    "publicAccountabilityStatement": {
      "type": "string",
      "description": "Statement for public accountability and transparency"
    }
  },
  "required": ["reportSummary", "stakeholderNotification", "escalationRequired", "actionPlan", "auditNotes", "publicAccountabilityStatement"]
}

