parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "programmeName": {
      "type": "string",
      "description": "Name of the programme"
    },
    "budgetStatus": {
      "type": "object",
      "properties": {
        "allocated": {"type": "number"},
        "spent": {"type": "number"},
        "remaining": {"type": "number"},
        "percentageUsed": {"type": "number"},
        "variance": {"type": "number"},
        "riskLevel": {"type": "string", "enum": ["Low", "Medium", "High", "Critical"]}
      }
    },
    "milestoneStatus": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "milestoneName": {"type": "string"},
          "status": {"type": "string", "enum": ["On Track", "At Risk", "Delayed", "Completed"]},
          "daysDelayed": {"type": "number"},
          "impact": {"type": "string"}
        }
      }
    },
    "performanceIndicators": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "kpiName": {"type": "string"},
          "target": {"type": "number"},
          "actual": {"type": "number"},
          "variance": {"type": "number"},
          "status": {"type": "string"}
        }
      }
    },
    "overallRiskScore": {
      "type": "number",
      "description": "Overall risk score from 0-100"
    },
    "criticalIssues": {
      "type": "array",
      "items": {"type": "string"}
    },
    "recommendations": {
      "type": "array",
      "items": {"type": "string"}
    }
  }
}

