// --------------------
// 1. Read inputs safely
// --------------------
const ticketsRaw = $json.tickets ?? [];

const previousNotes =
  $('Normalize Calendar and Notion Payloads').first()?.json?.previousMeetingNotes ?? '';

const meetingTitle =
  $('Format Calenda Event Payload').first()?.json?.summary ?? 'Meeting';

const eventStart =
  $('Format Calenda Event Payload').first()?.json?.eventStart;

const start = eventStart ? new Date(eventStart) : null;

// --------------------
// 2. Format meeting time
// --------------------
const meetingTime = start
  ? start.toLocaleString('en-IN', {
      weekday: 'short',
      day: '2-digit',
      month: 'short',
      hour: '2-digit',
      minute: '2-digit'
    })
  : '';

// --------------------
// 3. Clean & validate tickets
// --------------------
const validTickets = ticketsRaw.filter(
  t => t && t.source && t.id && t.title
);

// --------------------
// 4. Build Topics section
// --------------------
const ticketText = validTickets.length
  ? validTickets
      .map(t => `â€¢ *[${t.source}]* ${t.id} â€” ${t.title}`)
      .join('\n')
  : '_No tickets or PRs found._';

// --------------------
// 5. Build Previous Notes section
// --------------------
const notesText = previousNotes.trim()
  ? previousNotes
      .split('\n')
      .map(l => `â€¢ ${l.replace(/^[-â€¢]\s*/, '')}`)
      .join('\n')
  : '_No previous meeting notes found._';

// --------------------
