// --------------------
// 1. Read meeting context (single values)
// --------------------
const keywordsRaw =
  $('Extract Meeting Keywords').first().json.keywords || '';

const keywords = keywordsRaw
  .toLowerCase()
  .split(' ')
  .filter(Boolean);

const lastMeetingDateStr =
  $('Normalize Calendar and Notion Payloads').first().json.previousMeetingDate || null;

const lastMeetingDate = lastMeetingDateStr
  ? new Date(lastMeetingDateStr)
  : null;

// --------------------
// 2. Read PR list (multiple items)
// --------------------
const prs = $input.all();

// --------------------
// 3. Filter PRs
// --------------------
const filteredPRs = prs.filter(item => {
  const pr = item.json;

  // --- Date filter ---
  if (lastMeetingDate && pr.created_at) {
    const prCreatedAt = new Date(pr.created_at);
    if (prCreatedAt <= lastMeetingDate) {
      return false;
    }
  }

  // --- Keyword relevance ---
  const text = `${pr.title || ''} ${pr.body || ''}`.toLowerCase();

  const matchesKeyword = keywords.some(k => text.includes(k));

  return matchesKeyword;
});

// --------------------
// 4. Return filtered PRs
// --------------------
return filteredPRs;

const title = ($json.summary || '').trim();

const keywords = title
  .toLowerCase()
  .replace(/[^a-z0-9\s]/g, '') // remove symbols
  .split(' ')
  .filter(w => w.length > 2)   // drop short/noise words
  .slice(0, 5)
  .join(' ');

return {
  meetingTitle: title,
  keywords
};

const items = $input.all();

if (!items.length) {
  return [{ json: { tickets: [] } }];
}

const tickets = items.map(i => {
  const json = i.json;

  // GitHub PR / Issue
  if (json.html_url && json.number) {
    return {
      source: 'GitHub',
      id: `PR-${json.number}`,
      title: json.title,
      url: json.html_url
    };
  }

  // Jira Issue
  if (json.key && json.fields) {
    return {
      source: 'Jira',
      id: json.key,
      title: json.fields.summary,
      url: json.self
    };
  }

  // Fallback (unknown shape)
  return {};
});

return [
  {
    json: {
      tickets
    }
  }
];

const attendees = $input.first().json.attendees;

const message =
  $input.first().json.message;

if (!attendees || attendees.length === 0) {
  return [];
}

// Optional: filter declined attendees
const valid = attendees.filter(
  a => a.responseStatus !== 'declined'
);

return valid.map(a => ({
  json: {
    email: a.email,
    message
  }
}));
