// ============================================================
// CONFIG — Adjust these values to fit your setup
// ============================================================

// Optional: restrict to a specific n8n project ID.
// Leave empty to monitor ALL active workflows.
const PROJECT_ID = '';

// Tag name to exclude workflows from monitoring.
const SKIP_TAG = 'skip-monitoring';

// ============================================================
// AUTO-DISCOVERY — No changes needed below
// ============================================================

const workflows = $input.all().map(i => i.json);
const results = [];

for (const wf of workflows) {
  if (wf.isArchived) continue;

  // Project filter
  if (PROJECT_ID) {
    const wfProject = wf.shared?.[0]?.projectId;
    if (wfProject !== PROJECT_ID) continue;
  }

  // Tag filter
  const tags = (wf.tags || []).map(t => (t.name || t).toString().toLowerCase());
  if (tags.includes(SKIP_TAG)) continue;

  // Find trigger nodes
  const nodes = wf.nodes || [];
  const scheduleTrigger = nodes.find(n => n.type === 'n8n-nodes-base.scheduleTrigger');
  const pollingTrigger = nodes.find(n =>
    n.type.includes('Trigger') &&
    n.type !== 'n8n-nodes-base.manualTrigger' &&
    n.type !== 'n8n-nodes-base.executeWorkflowTrigger' &&
    n.type !== 'n8n-nodes-base.errorTrigger' &&
    n.type !== 'n8n-nodes-base.scheduleTrigger' &&
    n.parameters?.pollTimes
  );

  if (!scheduleTrigger && !pollingTrigger) continue;

  // Calculate max allowed age before alerting
  let maxAgeHours = 48; // safe default

  if (scheduleTrigger) {
    const interval = scheduleTrigger.parameters?.rule?.interval?.[0];
    if (interval) {
      if (interval.field === 'cronExpression' && interval.expression) {
        maxAgeHours = parseCronMaxAge(interval.expression);
      } else if (interval.field === 'minutes') {
        const mins = interval.minutesInterval || 30;
        maxAgeHours = Math.max(2, Math.ceil(mins * 8 / 60));
      } else if (interval.field === 'hours') {
        maxAgeHours = 48;
      } else if (interval.field === 'weeks') {
        maxAgeHours = 192; // 8 days
      } else if (interval.field === 'months') {
        maxAgeHours = 840; // 35 days
      } else {
        maxAgeHours = 48;
      }
    }
  }

  results.push({ json: { workflowId: wf.id, name: wf.name, maxAgeHours } });
}

function parseCronMaxAge(expr) {
  const parts = expr.trim().split(/\s+/);
  if (parts.length < 5) return 48;
  const [min, hour, dom, month, dow] = parts;

  // Monthly (e.g. "0 9 1 * *")
  if (dom !== '*' && !dom.startsWith('*/')) return 840;
  // Weekday-only (e.g. "0 8,13,18 * * 1-5") — 48h covers weekends
  if (dow !== '*') return 48;
  // Every N days (e.g. "0 9 */2 * *")
  if (dom.startsWith('*/')) {
    const n = parseInt(dom.split('/')[1]) || 1;
    return Math.max(48, n * 36);
  }
  // Sub-hourly (e.g. "*/15 8-17 * * *")
  if (min.startsWith('*/')) return 48;
  // Daily
  return 48;
}

return results;

const executions = $input.all().map(i => i.json);
const config = $('Discover Scheduled Workflows').all().map(i => i.json);

// Build lookup: workflowId -> latest execution
const execMap = {};
for (const exec of executions) {
  if (exec.workflowId) execMap[exec.workflowId] = exec;
}

const stale = [];
const ok = [];

for (const c of config) {
  const exec = execMap[c.workflowId];
  if (!exec || !exec.startedAt) {
    stale.push({ name: c.name, reason: 'No execution found' });
  } else {
    const ageHours = (Date.now() - new Date(exec.startedAt).getTime()) / (1000 * 60 * 60);
    if (ageHours > c.maxAgeHours) {
      stale.push({
        name: c.name,
        reason: `Last ran ${Math.round(ageHours)}h ago (max: ${c.maxAgeHours}h)`,
        lastRun: exec.startedAt
      });
    } else {
      ok.push(c.name);
    }
  }
}

return [{ json: { stale, staleCount: stale.length, okCount: ok.length, checkedCount: config.length } }];
