const data = $input.item.json;
const classification = data.classification || {};
const routing = data.routing || {};

const tags = [];

// Add creator status tag
if (classification.creator_status) {
  tags.push(`status:${classification.creator_status}`);
}

// Add creator tier tag
if (classification.creator_tier) {
  tags.push(`tier:${classification.creator_tier}`);
}

// Add primary platform tag
if (classification.primary_platform && classification.primary_platform !== 'unknown') {
  tags.push(`platform:${classification.primary_platform}`);
}

// Add primary niche tag
if (classification.primary_niche) {
  tags.push(`niche:${classification.primary_niche}`);
}

// Add routing flow tag
if (routing.flow) {
  tags.push(`flow:${routing.flow}`);
}

// Add creator type tags
if (classification.creator_type && Array.isArray(classification.creator_type)) {
  classification.creator_type.forEach(type => {
    tags.push(`type:${type}`);
  });
}

// Add intent signal tags
if (classification.intent_signals && Array.isArray(classification.intent_signals)) {
  classification.intent_signals.forEach(signal => {
    tags.push(`signal:${signal}`);
  });
}

// Add priority tag
if (routing.priority) {
  tags.push(`priority:${routing.priority}`);
}

return {
  json: {
    email: data.email,
    list_id: data.list_id,
    subscriber_id: data.subscriber_id,
    tags: tags,
    classification: classification,
    routing: routing
  }
};

const item = $input.item.json;
let email = null;
let firstName = null;
let lastName = null;
let tags = [];
let source = 'unknown';
let listId = null;
let subscriberId = null;

if (item.type === 'subscribe' && item.data) {
  email = item.data.email;
  firstName = item.data.merge_fields?.FNAME || null;
  lastName = item.data.merge_fields?.LNAME || null;
  listId = item.data.list_id;
  subscriberId = item.data.id;
  source = 'mailchimp';
} else if (item.contact) {
  email = item.contact.email;
  firstName = item.contact.first_name || null;
  lastName = item.contact.last_name || null;
  tags = item.contact.tags || [];
  source = 'activecampaign';
} else if (item.email) {
  email = item.email;
  source = 'direct';
}

if (!email) {
  throw new Error('No email found in trigger data');
}

return {
  json: {
    email: email.toLowerCase().trim(),
    first_name: firstName,
    last_name: lastName,
    tags: tags,
    source: source,
    list_id: listId,
    subscriber_id: subscriberId,
    timestamp: new Date().toISOString(),
    original_data: item
  }
};

function daysSinceDate(dateStr) {
  try {
    if (!dateStr) return null;
    const postDate = new Date(dateStr);
    if (isNaN(postDate.getTime())) return null;
    const now = new Date();
    return Math.ceil((now - postDate) / (1000 * 60 * 60 * 24));
  } catch {
    return null;
  }
}

function checkIfDormant(data) {
  const platforms = ['instagram', 'tiktok', 'youtube', 'twitter'];
  let mostRecentPostDays = null;

  for (const platform of platforms) {
    const platformData = data?.[platform];
    if (!platformData) continue;

    let lastPostDate = null;
    if (platform === 'instagram') lastPostDate = platformData.most_recent_post_date;
    if (platform === 'tiktok') lastPostDate = platformData.last_post_date;
    if (platform === 'youtube') lastPostDate = platformData.last_uploaded;

    if (lastPostDate) {
      const daysSince = daysSinceDate(lastPostDate);
      if (daysSince !== null && (mostRecentPostDays === null || daysSince < mostRecentPostDays)) {
        mostRecentPostDays = daysSince;
      }
    }
  }

  return mostRecentPostDays !== null && mostRecentPostDays >= 90;
}

function getMaxFollowers(data) {
  return Math.max(
    data?.instagram?.follower_count || 0,
    data?.tiktok?.follower_count || 0,
    data?.youtube?.subscriber_count || 0,
    data?.twitter?.follower_count || 0
  );
}

function determineRouting(classification, creatorData) {
  const routing = {
    flow: null,
    priority: 'standard',
    actions: [],
    notes: null,
    email_template: null,
    crm_actions: [],
    slack_alert: false
  };

  const status = classification.creator_status;
  const tier = classification.creator_tier;
  const intentSignals = classification.intent_signals || [];

  const isDormant = checkIfDormant(creatorData);
  const maxFollowers = getMaxFollowers(creatorData);
  const isHighValue = maxFollowers >= 50000;

  if (isDormant && isHighValue) {
    routing.flow = 'dormant_reactivation';
    routing.priority = 'medium-high';
    routing.slack_alert = true;
    return routing;
  }

  if (status === 'non_creator') {
    routing.flow = 'standard_newsletter';
    routing.priority = 'low';
    return routing;
  }

  if (status === 'aspiring_creator') {
    routing.flow = 'education_and_growth';
    routing.priority = 'medium';
    return routing;
  }

  if (status === 'active_creator') {
    if (tier === 'mid' || tier === 'macro') {
      routing.flow = 'partnership_team';
      routing.priority = 'high';
      routing.slack_alert = true;
      return routing;
    }

    if (tier === 'micro') {
      if (intentSignals.includes('has_brand_deals')) {
        routing.flow = 'fast_track_ambassador';
        routing.priority = 'high';
        routing.slack_alert = true;
        return routing;
      }

      routing.flow = 'affiliate_partnership';
      routing.priority = 'medium-high';
      return routing;
    }

    routing.flow = 'ambassador_program';
    routing.priority = 'medium';
  }

  if (!routing.flow) {
    routing.flow = 'standard_newsletter';
  }

  return routing;
}

const inputData = $input.item.json;
const classification = inputData.classification || inputData.output || {};
const creatorData = inputData.creator_data || inputData.creatorData || {};
const subscriber = inputData.subscriber || {};
const routing = determineRouting(classification, creatorData);

return {
  json: {
    email: subscriber.email || inputData.email,
    list_id: subscriber.list_id || inputData.list_id,
    subscriber_id: subscriber.subscriber_id || inputData.subscriber_id,
    classification,
    routing,
    timestamp: new Date().toISOString()
  }
};
