parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "employeeId": {
      "type": "string",
      "description": "Employee identifier"
    },
    "employeeName": {
      "type": "string",
      "description": "Employee full name"
    },
    "complianceStatus": {
      "type": "string",
      "enum": ["compliant", "at_risk", "non_compliant"],
      "description": "Overall compliance status"
    },
    "riskLevel": {
      "type": "string",
      "enum": ["low", "medium", "high", "critical"],
      "description": "Risk level based on compliance gaps"
    },
    "daysUntilExpiration": {
      "type": "number",
      "description": "Days until next expiration (negative if overdue)"
    },
    "requiredAction": {
      "type": "string",
      "enum": ["none", "reminder", "escalation"],
      "description": "Action required for this employee"
    },
    "trainingStatus": {
      "type": "string",
      "description": "Training completion status"
    },
    "certificationStatus": {
      "type": "string",
      "description": "Certification validity status"
    },
    "policyAcknowledgementStatus": {
      "type": "string",
      "description": "Policy acknowledgement status"
    },
    "reasoning": {
      "type": "string",
      "description": "Detailed explanation of the compliance assessment"
    }
  },
  "required": ["employeeId", "complianceStatus", "riskLevel", "requiredAction", "reasoning"]
}

parameters.jsCode:
const complianceData = $fromAI('complianceData', 'The compliance issue data requiring escalation', 'json');

try {
  const data = typeof complianceData === 'string' ? JSON.parse(complianceData) : complianceData;

  const riskLevel = data.riskLevel || 'medium';
  const daysOverdue = Math.abs(data.daysUntilExpiration || 0);
  const employeeName = data.employeeName || 'Unknown Employee';
  const employeeId = data.employeeId || 'Unknown ID';

  let escalationPath = [];
  let urgency = 'normal';
  let requiredActions = [];

  if (riskLevel === 'critical' || daysOverdue > 60) {
    escalationPath = ['Direct Manager', 'Department Head', 'HR Director', 'Compliance Officer'];
    urgency = 'immediate';
    requiredActions = [
      'Immediate manager notification',
      'HR intervention required',
      'Compliance review meeting',
      'Potential disciplinary action'
    ];
  } else if (riskLevel === 'high' || daysOverdue > 30) {
    escalationPath = ['Direct Manager', 'HR Representative'];
    urgency = 'high';
    requiredActions = [
      'Manager notification',
      'HR follow-up',
      'Compliance deadline extension review'
    ];
  } else {
    escalationPath = ['Direct Manager'];
    urgency = 'normal';
    requiredActions = [
      'Manager awareness',
      'Employee reminder'
    ];
  }

  const escalationReport = {
    employeeId: employeeId,
    employeeName: employeeName,
    riskLevel: riskLevel,
    daysOverdue: daysOverdue,
    urgency: urgency,
    escalationPath: escalationPath,
    requiredActions: requiredActions,
    escalationDate: new Date().toISOString(),
    summary: `${urgency.toUpperCase()} escalation required for ${employeeName} (${employeeId}). Risk: ${riskLevel}. Days overdue: ${daysOverdue}. Escalation path: ${escalationPath.join(' → ')}.`
  };

  return JSON.stringify(escalationReport, null, 2);

} catch (error) {
  return JSON.stringify({
    error: 'Escalation logic failed',
    message: error.message,
    escalationPath: ['HR Director'],
    urgency: 'high'
  });
}
