parameters.text:
=Cloud Spend Data:
Provider: {{ $json.cloudProvider }}
Total Spend: ${{ $json.totalSpend }}
Previous Month: ${{ $json.previousMonthSpend }}
Budget Limit: ${{ $json.budgetLimit }}

Top Services:
{{ JSON.stringify($json.topServices, null, 2) }}

Unused Resources:
{{ JSON.stringify($json.unusedResources, null, 2) }}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "totalSpend": {
      "type": "number",
      "description": "Total cloud spend amount"
    },
    "budgetStatus": {
      "type": "string",
      "description": "Budget status: within_budget, over_budget, or approaching_limit"
    },
    "spendTrend": {
      "type": "string",
      "description": "Spending trend: increasing, decreasing, or stable"
    },
    "percentageChange": {
      "type": "number",
      "description": "Percentage change from previous month"
    },
    "topCostDrivers": {
      "type": "array",
      "description": "Top 3 services driving costs",
      "items": {
        "type": "object",
        "properties": {
          "service": {"type": "string"},
          "cost": {"type": "number"},
          "impact": {"type": "string"}
        }
      }
    },
    "optimizationOpportunities": {
      "type": "array",
      "description": "List of cost optimization opportunities",
      "items": {
        "type": "object",
        "properties": {
          "opportunity": {"type": "string"},
          "estimatedSavings": {"type": "number"},
          "effort": {"type": "string"}
        }
      }
    },
    "unusedResourcesCount": {
      "type": "number",
      "description": "Number of unused resources identified"
    },
    "potentialSavings": {
      "type": "number",
      "description": "Total potential monthly savings"
    },
    "riskLevel": {
      "type": "string",
      "description": "Risk level: low, medium, high, or critical"
    },
    "analysisReasoning": {
      "type": "string",
      "description": "Detailed reasoning for the analysis"
    }
  },
  "required": ["totalSpend", "budgetStatus", "spendTrend", "percentageChange", "topCostDrivers", "optimizationOpportunities", "riskLevel", "analysisReasoning"]
}

parameters.text:
=Cost Intelligence Analysis:
{{ JSON.stringify($json.output, null, 2) }}

Configuration Thresholds:
Critical Threshold: ${{ $('Workflow Configuration').first().json.costThresholdCritical }}
High Threshold: ${{ $('Workflow Configuration').first().json.costThresholdHigh }}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "severity": {
      "type": "string",
      "description": "Severity level: critical, high, medium, or low"
    },
    "requiresFinanceEscalation": {
      "type": "boolean",
      "description": "Whether finance team escalation is required"
    },
    "alertMessage": {
      "type": "string",
      "description": "Formatted alert message for notifications"
    },
    "recommendedActions": {
      "type": "array",
      "description": "List of recommended immediate actions",
      "items": {
        "type": "string"
      }
    },
    "escalationReason": {
      "type": "string",
      "description": "Reason for escalation if applicable"
    },
    "governanceDecision": {
      "type": "string",
      "description": "Detailed governance decision and rationale"
    }
  },
  "required": ["severity", "requiresFinanceEscalation", "alertMessage", "recommendedActions", "governanceDecision"]
}
